import React from 'react';
import { Route, Switch, Redirect } from 'react-router-dom';
import './App.css';

const api = "http://jsonplaceholder.typicode.com/photos?_limit=20";

class App extends Component {

  constructor(props){
    super(props);
    this.state = {photos:[]}
  }

  componentDidMount(){
    fetch(api)
    .then(response => response.json())
    .then((photos) => {
      this.setState({photos:photos})
    })
    .catch(err => {
      console.log(err)
    })
  }

  render() {
    return (
      <section>
        <h1>Gallery</h1>
        <p>Lorem ipsum dolor sit amet</p>
        <div className="gallery">
          {
            this.state.photos.map(photo => {
              return <div className="photo"><img src={photo.thumbnailUrl} data-id={photo.id} /></div>
            })
          }
        </div>
      </section>
    );
  }
}

export default App;
